package basic;

public class IfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		if (a == 1) {
			System.out.println(" value is :1");
		} else if (a == 2) {
			System.out.println(" value is :2");

		} else if (a == 3) {
			System.out.println(" value is :3");
		} else {
			System.out.println("value is not :1,2,3");
		}

	}

}
