package basic;

public class SwitchDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='&';
		switch (ch) {
		case 'a':
			System.out.println(" char value is "+ch);
			break;
		case 'b':
			System.out.println(" char value is "+ch);
			break;
		case 'c':
			System.out.println(" char value is "+ch);
			break;
		case 'd':
			System.out.println(" char value is "+ch);
			break;
		case 'e':
			System.out.println(" char value is "+ch);
			break;

		default:
			System.out.println(" other value");
			break;
		}

	}

}
